import numpy as npimport matplotlib.pyplot as pltdef main():    fig = plt.figure(1, facecolor='w')    ax = fig.add_subplot(111)    ax.axis([0, 10, 0, 10])  # set the range of axis    remove_axis(ax)    fig.show()    plt.draw()    the_draw(fig)    plt.pause(120)def the_draw(fig):    x = np.linspace(0,10,11)    for i in np.linspace(0, 2, 21):        y = x*i        plt.plot(x, y)        plt.pause(0.01)def remove_axis(ax):    ax.spines['top'].set_visible(False)  # set top x axis invisible    ax.spines['right'].set_visible(False)  # SET right y axis invisible    ax.xaxis.tick_bottom()    ax.yaxis.tick_left()if __name__ == '__main__':    main()